import { Component } from '@angular/core';
import { ExtractionPdfService } from '../core/services/extraction-pdf.service';
import { HttpClientModule } from '@angular/common/http';
import { CommonModule } from '@angular/common';
@Component({
  selector: 'app-extraction-pdf',
  standalone: true,
  imports: [HttpClientModule, CommonModule],
  templateUrl: './extraction-pdf.component.html',
  styleUrl: './extraction-pdf.component.scss'
})
export class ExtractionPDFComponent {
  selectedFile: File = null;
  uploadResponse: any = null;

  constructor(private policyService: ExtractionPdfService) {}

  onFileSelected(event): void {
    this.selectedFile = event.target.files[0];
  }

  uploadFile(): void {
    if (this.selectedFile) {
      this.policyService.uploadPdf(this.selectedFile).subscribe(
        response => {
          console.log(response);
          this.uploadResponse = response;
        },
        error => console.log(error)
      );
    }
  }
}
<div class="file-upload-wrapper">
  <input type="file" id="pdfFile" (change)="onFileSelected($event)" hidden>
  <label for="pdfFile" class="file-upload-icon">
      <i class="fas fa-file-pdf"></i> <!-- Assurez-vous d'avoir FontAwesome pour utiliser cette icône -->
  </label>
  <button class="upload-btn" (click)="uploadFile()">Upload PDF</button>
</div>

<div *ngIf="uploadResponse" class="upload-response">
  <p>Response: {{ uploadResponse | json }}</p>
</div>
